export function fileActions(path: string, $: CheerioAPI) {
  const id = randomUUID();
  $('body').append(`<script>
  document.getElementById('${id}').addEventListener('click', function() {
    navigator.clipboard.writeText('${path}');
    showSnackbar();
  });
</script>`);

  return `
<div class="file-actions visible-on-hover">
    <button class="unstyled" id="${id}" title="Copy file path">
    ${copySvg}
</button>
    <a title="Open in Visual Studio Code" href="vscode://file/${path}">${vscodeSvg}</a>
<!--    <a href="jetbrains://webstorm/navigate/reference?path=${path}">${webstormSvg}</a>-->
<!--    <a href="jetbrains://idea/navigate/reference?path=${path}">${intelijSvg}</a>-->
</div>`;
}

